import React, { useState, useEffect, setState } from 'react';
import Editor from './Editor'
import MenuRenderer from './MenuRenderer'
import Cookies from 'js-cookie';
import Configuration from './Helpers'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes, faCheck } from '@fortawesome/free-solid-svg-icons'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import TemplateList from './TemplateList';
import WidgetList from './WidgetList';
function TemplateGenerator() {

const [template, setTemplate] = useState('')
const [scripts, setScripts] = useState('')
const [templateName, setTemplateName] = useState('')
const [templateId, setTemplateId] = useState('')
const [parsedTemplate, setParsedTemplate] = useState('')
const [properties, setProperties] = useState('')
const [time, setTime] = useState('')
const [userData, setUserData] = useState('')
const [dataId, setDataId] = useState('')
const [dataset, setDataset] = useState('')
const [metaData, setMetaData] = useState('')
const [widgetName, setWidgetName] = useState('')
const [widgetId, setWidgetId] = useState('')




function saveWidget(){
  var csrftoken = Cookies.get('csrftoken');
  var body = JSON.stringify({
    'scripts':scripts,
    'widgetName':widgetName,
    'sourceid': templateName,
    'templateid':templateId,
    'widgetid':widgetId
  })
  fetch('save_widget', {
    method: 'POST',
    credentials: 'include',
    mode: 'same-origin',
    headers: {
      'Accept': 'application/json',
      'X-CSRFToken':csrftoken
    },
    body: body
  }).then(response => response.text())
  .then(data => setWidgetId(data))

}
function saveTemplate(){
  var csrftoken = Cookies.get('csrftoken');
  var body = JSON.stringify({
    'scripts':scripts,
    'template':template,
    'templateName': templateName,
    'templateId':templateId,
  })
  fetch('save_template', {
    method: 'POST',
    credentials: 'include',
    mode: 'same-origin',
    headers: {
      'Accept': 'application/json',
      'X-CSRFToken':csrftoken
    },
    body: body
  }).then(response => response.text())
  .then(data => setTemplateId(data))

}
function loadTemplate(id){
  fetch('get_template?templateid='+id)
  .then(response => response.text())
  .then(data => {
    var templateData = JSON.parse(data)
    setTemplate(templateData[0].fields.template)
    setScripts(templateData[0].fields.scripts)
    setTemplateId(templateData[0].fields.templateid)
    setTemplateName(templateData[0].fields.name)
    var newProperties = {}
    for (var userInput of templateData[0].parsed){
      newProperties[userInput.name] = {type: userInput.type, value:''}
    }
    console.log("setting props",newProperties)
    setProperties(newProperties, setTimeout(function(){setParsedTemplate(templateData[0].parsed)},100))
    
  })
}
function loadWidget(id){
  fetch('get_widget?widgetid='+id)
  .then(response => response.text())
  .then(data => {
    var templateData = JSON.parse(data)
    loadTemplate(templateData[0].fields.templateid)
    getMetaData(templateData[0].fields.sourceid)    
    setProperties(templateData[0].fields.widget)
  })  
}
function processScript(){
  var csrftoken = Cookies.get('csrftoken'); 
  var body = JSON.stringify({'properties':properties,'script':template, 'sourceid':dataId})
  fetch('get_script',{
    method: 'POST',
    credentials: 'include',
    mode: 'same-origin',
    headers: {
      'Accept': 'application/json',
      'X-CSRFToken':csrftoken
    },
    body:body
  })
  .then(response => response.text())
  .then(txt => {
    var dataSet = dataset
    var scriptURLs = scripts.split(/\r?\n/);
    for (var scriptURL of scriptURLs){
      var script = document.createElement('script');
      script.type = 'text/javascript';
      script.async = true;
      script.onload = function(){
      };
      script.src = scriptURL;
      document.getElementsByTagName('head')[0].appendChild(script);
    }
    eval(txt)
  })
}
function runTemplate(){
  var csrftoken = Cookies.get('csrftoken'); 
  var body = JSON.stringify({'properties':properties, 'metadata':metaData, 'sourceid':dataId})
  fetch('get_data',{
    method: 'POST',
    credentials: 'include',
    mode: 'same-origin',
    headers: {
      'Accept': 'application/json',
      'X-CSRFToken':csrftoken
    },
    body:body
  })
  .then(response => response.json())
  .then(data => {
    console.log(data)
    setDataset(data,   processScript())
  })
}
function saveProperties(property, type, value){
  var propertyCopy = properties
  if (!propertyCopy){
    propertyCopy={}
  }
  if (!type) type=''
  if (!value) value=''
  propertyCopy[property] = {type: type, value: value};
  setProperties(propertyCopy);
}
function saveTemplateName(e){
  setTemplateName(e.target.value);
}
function getDataset(e){


}
function getMetaData(datasetId){
  fetch('get_metadata?sourceid='+datasetId)
  .then(response => response.json())
  .then(data => {
    setMetaData(data)
    setDataId(datasetId)
  })
}
function selectData(e){
  getMetaData(e.target.getAttribute("uuid"))
}
useEffect(() => {
  fetch('get_user_data')
.then(response => response.json())
.then(data => setUserData(data))
}, [])

let datasets = []
if (userData){
  for (const dataset of userData){
      let dset = <div uuid={dataset.sourceid} onClick={selectData} className="reportRow">{dataset.name}</div>
      datasets.push(dset)
  }
}
return (
  <div id="templater">
      <div className="templateContainer"> 
        {datasets}
        <TemplateList loadTemplate={loadTemplate}/>
        <WidgetList loadWidget={loadWidget} />
        <div className="parseButton" onClick={saveTemplate}>
          Save Template
        </div>
        <input type="text" defaultValue="New Template" value={templateName} onChange={saveTemplateName}></input>
        <Editor
          language="javascript"
          displayName="Scripts"
          value={scripts}
          onChange={setScripts}  
        />
        <Editor
          language="javascript"
          displayName="Chart Template"
          value={template}
          onChange={setTemplate}  
        />
      </div>
      <div className="optionsContainer">
        <div className="parseButton" onClick={runTemplate}>
          Run JS
        </div>
        <MenuRenderer  parsedTemplate={parsedTemplate} properties={properties} saveProperties={saveProperties} metaData={metaData} key={metaData}/>      
      </div>
      <div className="renderContainer">
        <div id="container" ></div>
      </div>

  </div>
)
}
export default TemplateGenerator;
