export class InputOption extends React.Component {
    constructor(props) {
      super(props);
      this.saveGroupSetting = this.saveGroupSetting.bind(this)
    }
    saveGroupSetting(e){
        if (e.target.type == "checkbox"){
            this.props.save(this.props.part, this.props.name, e.target.checked)
        }else{
            this.props.save(this.props.part, this.props.name, e.target.value)
        }
    }
    render() {
      if (!this.props.hideLabel){
        return(
            <div className="row" >
                <div className="col desc">{this.props.desc}</div>
                <div className="col">
                    <input onChange={this.saveGroupSetting} className={this.props.cName} value={this.props.value} type={this.props.type} id={this.props.name} name={this.props.name}/>
                </div>
            </div>
          )
      }else{
        return(
            <input onChange={this.saveGroupSetting} className={this.props.cName} checked={this.props.value} value={this.props.value} type={this.props.type} id={this.props.name} name={this.props.name}/>
        )  
      }

    }
}
export class ColorPicker extends React.Component{
      state = {
        background: this.props.value,
        popupShown: false,
      };
    
      handleChangeComplete = (color) => {
        this.props.save(this.props.part, this.props.name, color.hex)
        this.setState({ 
            background: color.hex,
            popupShown:false, 
        });
      };
      heandleShowPicker  = () => {
        this.setState({
            popupShown:true,
        })
      }
      render() {
          let colorPicker = {
            backgroundColor: this.state.background,
            width: "25px",
            height: "25px",
            border: "1px solid #eee",
            padding: "1px 2px"

          };
          if (this.state.popupShown){
            return (
                <div className="row" >
                    <div className="col desc">{this.props.desc}</div>
                    <SketchPicker
                    color={ this.state.background }
                    onChangeComplete={ this.handleChangeComplete }
                    />
                </div>
            );
          }else{
            return (
                <div className="row" >
                    <div className="col desc">{this.props.desc}</div>
                <div onClick={this.heandleShowPicker} style={colorPicker}></div> 
               </div>         
            )
        }

      }
}
