import {InputOption, ColorPicker} from './Util'
import Cookies from 'js-cookie'

export class LoginPage extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        username: '',
        password: ''
      }
      this.saveUserSetting = this.saveUserSetting.bind(this)
      this.loginUser = this.loginUser.bind(this)

    }
    loginUser(){
      var csrftoken = Cookies.get('csrftoken');
      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json','X-CSRFToken': csrftoken },
        body: JSON.stringify({user: this.state.loginUsername, password: this.state.loginPassword})
      };
      fetch('loginUser', requestOptions)
          .then(response => {
            if (response.ok){
              console.log("success!!")
              this.props.loginEvent();
            }else{
              console.log("failed")
            }
          })
    }
    saveUserSetting(part,setting,value){
      let stateObj = this.state
      stateObj[setting] = value
      this.setState(stateObj)
    }
    render() {
      return(
        <div className="content loginContent">
          <div className="section loginSection">
            <div className="leftHeader">Login User</div>
            <div className="paddedContent">
              <div className="col desc">Username</div>
              <div className="col"><InputOption hideLabel="true" type="text"  name="loginUsername" value={this.state.loginUsername} save={this.saveUserSetting}/></div>
              <div className="col desc">Password</div>
              <div className="col"><InputOption hideLabel="true" type="text"  name="loginPassword" value={this.state.loginPassword} save={this.saveUserSetting}/></div>
              <div className="col"><div className="saveButtonSmall" id="titleSave" onClick={this.loginUser}>Login</div></div>
            </div>
          </div>
          </div>
      )
  
    }
  }
  export class RegisterPage extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        username: '',
        password: ''
      }
      this.saveUserSetting = this.saveUserSetting.bind(this)
      this.createUser = this.createUser.bind(this)

    }
    createUser(){
      var csrftoken = Cookies.get('csrftoken');

      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json','X-CSRFToken': csrftoken },
        body: JSON.stringify({user: this.state.createUsername, password: this.state.createPassword})
      };
      fetch('addUser', requestOptions)
          .then(response => response.json())
          .then(data => console.log(data));     
    }
    saveUserSetting(part,setting,value){
      let stateObj = this.state
      stateObj[setting] = value
      this.setState(stateObj)
    }
    render() {
      return(
        <div className="content loginContent">
            <div className="section loginSection">
              <div className="leftHeader">Create User</div>
              <div className="paddedContent">
                <div className="col desc">Username</div>
                <div className="col"><InputOption hideLabel="true" type="text" name="createUsername" value={this.state.createUsername} save={this.saveUserSetting}/></div>
                <div className="col desc">Password</div>
                <div className="col"><InputOption hideLabel="true" type="text" name="createPassword" value={this.state.createPassword} save={this.saveUserSetting}/></div>
                <div className="col"><div className="saveButtonSmall" id="titleSave" onClick={this.createUser}>Create</div></div>
              </div>
            </div>
          </div>
      )
  
    }
  }
