import React, { useState, useEffect, setState } from 'react';
import Editor from './Editor'
import Configuration from './Helpers'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes, faCheck } from '@fortawesome/free-solid-svg-icons'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function MenuRenderer() {

const [html, setHtml] = useState('')



const YELLOWFIN_URL="http://localhost:8080"


function changeHandler(){
    console.log("value changed")
}

var inputDefinitions = [{'name': 'title', 'type': 'String'}, {'name': 'suffix', 'type': 'String'}]
var inputMenu = []
for (var userInput of inputDefinitions){
    if (userInput.type=='String'){
        inputMenu.push(<TextInput value="" inputName={userInput.name} onChange={changeHandler}/>)
    }
}


return (
  <div id="container">
      {inputMenu}
  </div>
)
}




function TextInput(props) {
  const {
      value,
      inputName,
      onChange
  } = props

  function handleChange(e) {
      onChange(e.target.value);
  }
  return (
      <div >
          <div>{inputName}</div>
          <input type="text" value={value} onChange={handleChange}></input>
      </div>
  )

}
export default MenuRenderer;
