import React, { useState, useEffect, setState } from 'react';

function APIConfig(props){
    const [connectionName, setConnectionName] = useState('')
    const [URL, setURL] = useState('')
    const [URLParameters, setURLParameters] = useState('')
    const [userData, setUserData] = useState('')
    const [paramKey, setParamKey] = useState('')

    useEffect(() => {

    }, [])
    function saveURLParameter(idx, type, value){
        var paramCopy = URLParameters
        console.log("paramUpdate",idx, type, value)
        if (type=='val'){
            paramCopy[idx].value = value
        }else{
            paramCopy[idx].key = value
        }
        setURLParameters(paramCopy)
    }
    function addParameter(){
        var paramCopy = URLParameters
        if (!paramCopy){
            paramCopy=[]
        }
        paramCopy.push({'idx':paramCopy.length,'key':'','value':''})
        setURLParameters(paramCopy)
        setParamKey(new Date())
    }
    var urlParams = []
    console.log("initing",URLParameters)
    for (var param in URLParameters){
        console.log("initinga",param.idx,param.idx)
        urlParams.push(
            <URLParameter saveURLParameter={saveURLParameter} idx={param.idx} defaultValue={param.value} defaultParam={param.key} />
        )
    }
    return(
        <div className="contentCol">
            <div>Connection Name</div>
            <input type="text" value={connectionName} onChange={setConnectionName}></input>
            <div>Endpoint URL</div>
            <input type="text" value={URL} onChange={setURL}></input>
            <div>Endpoint Parameters</div>
            <div onClick={addParameter}>Add Parameter</div>
            {urlParams}
            <div>Save Connection</div>
        </div>
    )
  }

  function URLParameter(props){
    const [parameter, setParameter] = useState('')
    const [value, setValue] = useState('')
    const {
        saveURLParameter,
        idx,
        defaultValue,
        defaultParam
        
    } = props
    console.log("initingz",idx)
    function setParam(e){
        console.log("setting", idx, e.target.value)
        saveURLParameter(idx, 'param', e.target.value)
        setParameter(e.target.value)
    }
    function setVal(e){
        saveURLParameter(idx, 'val', e.target.value)
        setValue(e.target.value)
    }  
    return(
        <div>
            <input type="text" value={parameter} onChange={setParam}></input>
            <input type="text" value={value} onChange={setVal}></input>
        </div>
    )
  }  
  export default APIConfig;
